import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
  color: #fff;
  padding: 2rem;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 2rem;
  background: linear-gradient(90deg, #00f5a0, #00d9f5);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

const CourseGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const CourseCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 15px;
  padding: 1.5rem;
  transition: transform 0.3s ease;
  border: 1px solid rgba(255, 255, 255, 0.1);

  &:hover {
    transform: translateY(-5px);
  }
`;

const CourseTitle = styled.h2`
  font-size: 1.25rem;
  margin-bottom: 1rem;
  color: #00f5a0;
`;

const CourseInfo = styled.p`
  color: #fff;
  margin: 0.5rem 0;
  font-size: 1rem;
`;

function App() {
    const [courses, setCourses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCourses = async () => {
            try {
                const response = await axios.get('http://localhost:3007/api/courses');
                setCourses(response.data.slice(0, 10));
                setLoading(false);
            } catch (error) {
                console.error('Feil ved henting av kursdata:', error);
                setError('Kunne ikke hente kursdata. Vennligst prøv igjen senere.');
                setLoading(false);
            }
        };

        fetchCourses();
    }, []);

    if (loading) {
        return (
            <AppContainer>
                <Title>Laster kurs...</Title>
            </AppContainer>
        );
    }

    if (error) {
        return (
            <AppContainer>
                <Title>{error}</Title>
            </AppContainer>
        );
    }

    return (
        <AppContainer>
            <Title>Kommende Kurs</Title>
            <CourseGrid>
                {courses.map(course => (
                    <CourseCard key={course.id}>
                        <CourseTitle>{course.title}</CourseTitle>
                        <CourseInfo>
                            <strong>Dato:</strong> {new Date(course.startDate).toLocaleDateString('nb-NO')}
                        </CourseInfo>
                        <CourseInfo>
                            <strong>Påmeldte:</strong> {course.participantCount} / {course.maxParticipants}
                        </CourseInfo>
                        <CourseInfo>
                            <strong>Sted:</strong> {course.location}
                        </CourseInfo>
                    </CourseCard>
                ))}
            </CourseGrid>
        </AppContainer>
    );
}

export default App;
